            if (!$this->check_required_credentials(false)) {
                return null;
            }
            $response = Async\await($this->privateGetV5AssetCoinQueryInfo ($params));
            //
            //     {
            //         "retCode" => 0,
            //         "retMsg" => "",
            //         "result" => {
            //             "rows" => array(
            //                 {
            //                     "name" => "BTC",
