            $this->check_required_symbol('fetchOHLCV', $symbol);
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            if ($limit === null) {
                $limit = 200; // default is 200 when requested with `$since`
            }
            if ($since !== null) {
                $request['start'] = $since;
            }
            if ($limit !== null) {
                $request['limit'] = $limit; // max 1000, default 1000
            }
