    public function fetch_funding_rate_history(?string $symbol = null, ?int $since = null, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * fetches historical funding rate prices
             * @see https://bybit-exchange.github.io/docs/v5/market/history-fund-rate
             * @param {string|null} $symbol unified $symbol of the $market to fetch the funding rate history for
             * @param {int|null} $since $timestamp in ms of the earliest funding rate to fetch
             * @param {int|null} $limit the maximum amount of ~@link https://docs.ccxt.com/en/latest/manual.html?#funding-rate-history-structure funding rate structures~ to fetch
             * @param {array} $params extra parameters specific to the bybit api endpoint
             * @param {int|null} $params->until $timestamp in ms of the latest funding rate
             * @return {[array]} a list of ~@link https://docs.ccxt.com/en/latest/manual.html?#funding-rate-history-structure funding rate structures~
             */
            $this->check_required_symbol('fetchFundingRateHistory', $symbol);
            Async\await($this->load_markets());
            if ($limit === null) {
                $limit = 200;
            }
            $request = array(
                // 'category' => '', // Product type. linear,inverse
                // 'symbol' => '', // Symbol name
                // 'startTime' => 0, // The start $timestamp (ms)
                // 'endTime' => 0, // The end $timestamp (ms)
                'limit' => $limit, // Limit for data size per page. [1, 200]. Default => 200
            );
            $market = $this->market($symbol);
            $symbol = $market['symbol'];
            $request['symbol'] = $market['id'];
            if ($market['option']) {
                throw new NotSupported($this->id . ' fetchFundingRateHistory() is not supported for option markets');
            } elseif ($market['linear']) {
                $request['category'] = 'linear';
            } elseif ($market['inverse']) {
                $request['category'] = 'inverse';
            }
            if ($since !== null) {
                $request['startTime'] = $since;
            }
            $until = $this->safe_integer_2($params, 'until', 'till'); // unified in milliseconds
            $endTime = $this->safe_integer($params, 'endTime', $until); // exchange-specific in milliseconds
            $params = $this->omit($params, array( 'endTime', 'till', 'until' ));
            if ($endTime !== null) {
                $request['endTime'] = $endTime;
            } else {
                if ($since !== null) {
                    // end time is required when $since is not empty
                    $fundingInterval = 60 * 60 * 8 * 1000;
                    $request['endTime'] = $since . $limit * $fundingInterval;
                }
            }
            $response = Async\await($this->publicGetV5MarketFundingHistory (array_merge($request, $params)));
            //
            //     {
            //         "retCode" => 0,
            //         "retMsg" => "OK",
            //         "result" => {
            //             "category" => "linear",
            //             "list" => array(
            //                 array(
            //                     "symbol" => "ETHPERP",
            //                     "fundingRate" => "0.0001",
            //                     "fundingRateTimestamp" => "1672041600000"
            //                 }
            //             )
            //         ),
            //         "retExtInfo" => array(),
            //         "time" => 1672051897447
            //     }
            //
            $rates = array();
            $result = $this->safe_value($response, 'result');
            $resultList = $this->safe_value($result, 'list');
            for ($i = 0; $i < count($resultList); $i++) {
                $entry = $resultList[$i];
                $timestamp = $this->safe_integer($entry, 'fundingRateTimestamp');
                $rates[] = array(
                    'info' => $entry,
                    'symbol' => $this->safe_symbol($this->safe_string($entry, 'symbol'), null, null, 'swap'),
                    'fundingRate' => $this->safe_number($entry, 'fundingRate'),
                    'timestamp' => $timestamp,
                    'datetime' => $this->iso8601($timestamp),
                );
            }
            $sorted = $this->sort_by($rates, 'timestamp');
            return $this->filter_by_symbol_since_limit($sorted, $symbol, $since, $limit);
        }) ();
    }

    public function parse_trade($trade, $market = null) {
        $isSpotTrade = (is_array($trade) && array_key_exists('isBuyerMaker', $trade)) || (is_array($trade) && array_key_exists('feeTokenId', $trade));
        if ($isSpotTrade) {
            return $this->parse_spot_trade($trade, $market);
        } else {
            return $this->parse_contract_trade($trade, $market);
        }
    }

    public function parse_spot_trade($trade, $market = null) {
        //
        //   public:
        //     {
        //        "price" => "39548.68",
        //        "time" => "1651748717850",
        //        "qty" => "0.166872",
        //        "isBuyerMaker" => 0
        //     }
        //
        //   private:
        //     {
        //         "orderPrice" => "82.5",
        //         "creatTime" => "1666702226326",
        //         "orderQty" => "0.016",
        //         "isBuyer" => "0",
        //         "isMaker" => "0",
        //         "symbol" => "AAVEUSDT",
        //         "id" => "1274785101965716992",
        //         "orderId" => "1274784252359089664",
        //         "tradeId" => "2270000000031365639",
        //         "execFee" => "0",
        //         "feeTokenId" => "AAVE",
        //         "matchOrderId" => "1274785101865076224",
        //         "makerRebate" => "0",
        //         "executionTime" => "1666702226335"
        //     }
        //
        $timestamp = $this->safe_integer_n($trade, array( 'time', 'creatTime' ));
        $takerOrMaker = null;
        $side = null;
        $isBuyerMaker = $this->safe_integer($trade, 'isBuyerMaker');
        if ($isBuyerMaker !== null) {
            // if public response
            $side = ($isBuyerMaker === 1) ? 'buy' : 'sell';
        } else {
            // if private response
            $isBuyer = $this->safe_integer($trade, 'isBuyer');
            $isMaker = $this->safe_integer($trade, 'isMaker');
            $takerOrMaker = ($isMaker === 0) ? 'maker' : 'taker';
            $side = ($isBuyer === 0) ? 'buy' : 'sell';
        }
        $marketId = $this->safe_string($trade, 'symbol');
        $market = $this->safe_market($marketId, $market, null, 'spot');
        $fee = null;
        $feeCost = $this->safe_string($trade, 'execFee');
        if ($feeCost !== null) {
            $feeToken = $this->safe_string($trade, 'feeTokenId');
            $feeCurrency = $this->safe_currency_code($feeToken);
            $fee = array(
                'cost' => $feeCost,
                'currency' => $feeCurrency,
            );
        }
        return $this->safe_trade(array(
            'id' => $this->safe_string($trade, 'tradeId'),
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $market['symbol'],
            'order' => $this->safe_string($trade, 'orderId'),
            'type' => null,
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $this->safe_string_2($trade, 'price', 'orderPrice'),
            'amount' => $this->safe_string_2($trade, 'qty', 'orderQty'),
            'cost' => null,
            'fee' => $fee,
        ), $market);
    }

    public function parse_contract_trade($trade, $market = null) {
        //
        // public contract
        //
        //     {
        //         "execId" => "666042b4-50c6-58f3-bd9c-89b2088663ff",
        //         "symbol" => "ETHUSD",
        //         "price" => "1162.95",
        //         "size" => "1",
        //         "side" => "Sell",
        //         "time" => "1669191277315",
        //         "isBlockTrade" => false
        //     }
        //
        // public unified margin
        //
        //     {
        //         "execId" => "da66abbc-f358-5864-8d34-84ef7274d853",
        //         "symbol" => "BTCUSDT",
        //         "price" => "20802.50",
        //         "size" => "0.200",
        //         "side" => "Sell",
        //         "time" => "1657870316630"
        //     }
        //
        // private contract trades
        //
        //     {
        //         "symbol" => "ETHUSD",
        //         "execFee" => "0.00005484",
        //         "execId" => "acf78206-d464-589b-b888-51bd130821c1",
        //         "execPrice" => "1367.80",
        //         "execQty" => "100",
        //         "execType" => "Trade",
        //         "execValue" => "0.0731101",
        //         "feeRate" => "0.00075",
        //         "lastLiquidityInd" => "RemovedLiquidity",
        //         "leavesQty" => "0",
        //         "orderId" => "fdc584c3-be5d-41ff-8f54-5be7649b1d1c",
        //         "orderLinkId" => "",
        //         "orderPrice" => "1299.50",
        //         "orderQty" => "100",
        //         "orderType" => "Market",
        //         "stopOrderType" => "UNKNOWN",
        //         "side" => "Sell",
        //         "execTime" => "1611528105547",
        //         "closedSize" => "100"
        //     }
        //
        // private unified margin
        //
        //     {
        //         "symbol" => "AAVEUSDT",
        //         "id" => "1274785101965716992",
        //         "orderId" => "1274784252359089664",
        //         "tradeId" => "2270000000031365639",
        //         "orderPrice" => "82.5",
        //         "orderQty" => "0.016",
        //         "execFee" => "0",
        //         "feeTokenId" => "AAVE",
        //         "creatTime" => "1666702226326",
        //         "isBuyer" => "0",
        //         "isMaker" => "0",
        //         "matchOrderId" => "1274785101865076224",
        //         "makerRebate" => "0",
        //         "executionTime" => "1666702226335"
        //     }
        //
        // private USDC settled trades
        //
        //     {
        //         "symbol" => "ETHPERP",
        //         "orderLinkId" => "",
        //         "side" => "Buy",
        //         "orderId" => "aad0ee44-ce12-4112-aeee-b7829f6c3a26",
        //         "execFee" => "0.0210",
        //         "feeRate" => "0.000600",
        //         "blockTradeId" => "",
        //         "tradeTime" => "1669196417930",
        //         "execPrice" => "1162.15",
        //         "lastLiquidityInd" => "TAKER",
        //         "execValue" => "34.8645",
        //         "execType" => "Trade",
        //         "execQty" => "0.030",
        //         "tradeId" => "0e94eaf5-b08e-5505-b43f-7f1f30b1ca80"
        //     }
        //
        $id = $this->safe_string_n($trade, array( 'execId', 'id', 'tradeId' ));
        $marketId = $this->safe_string($trade, 'symbol');
        $marketType = 'contract';
        if ($market !== null) {
            $marketType = $market['type'];
        }
        $category = $this->safe_string($trade, 'category');
        if ($category !== null) {
            if ($category === 'spot') {
                $marketType = 'spot';
            }
        }
        $market = $this->safe_market($marketId, $market, null, $marketType);
        $symbol = $market['symbol'];
        $amountString = $this->safe_string_n($trade, array( 'execQty', 'orderQty', 'size' ));
        $priceString = $this->safe_string_n($trade, array( 'execPrice', 'orderPrice', 'price' ));
        $costString = $this->safe_string($trade, 'execValue');
        $timestamp = $this->safe_integer_n($trade, array( 'time', 'execTime', 'tradeTime' ));
        $side = $this->safe_string_lower($trade, 'side');
        if ($side === null) {
            $isBuyer = $this->safe_integer($trade, 'isBuyer');
            if ($isBuyer !== null) {
                $side = $isBuyer ? 'buy' : 'sell';
            }
        }
        $isMaker = $this->safe_value($trade, 'isMaker');
        $takerOrMaker = null;
        if ($isMaker !== null) {
            $takerOrMaker = $isMaker ? 'maker' : 'taker';
        } else {
            $lastLiquidityInd = $this->safe_string($trade, 'lastLiquidityInd');
            if ($lastLiquidityInd === 'UNKNOWN') {
                $lastLiquidityInd = null;
            }
            if ($lastLiquidityInd !== null) {
                if (($lastLiquidityInd === 'TAKER') || ($lastLiquidityInd === 'MAKER')) {
                    $takerOrMaker = strtolower($lastLiquidityInd);
                } else {
                    $takerOrMaker = ($lastLiquidityInd === 'AddedLiquidity') ? 'maker' : 'taker';
                }
            }
        }
        $orderType = $this->safe_string_lower($trade, 'orderType');
        if ($orderType === 'unknown') {
            $orderType = null;
        }
        $feeCostString = $this->safe_string($trade, 'execFee');
        $fee = null;
        if ($feeCostString !== null) {
            $feeCurrencyCode = null;
            if ($market['spot']) {
                $feeCurrencyCode = $this->safe_string($trade, 'commissionAsset');
            } else {
                $feeCurrencyCode = $market['inverse'] ? $market['base'] : $market['settle'];
            }
            $fee = array(
                'cost' => $feeCostString,
                'currency' => $feeCurrencyCode,
            );
        }
        return $this->safe_trade(array(
            'id' => $id,
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'order' => $this->safe_string($trade, 'orderId'),
            'type' => $orderType,
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $priceString,
            'amount' => $amountString,
            'cost' => $costString,
            'fee' => $fee,
        ), $market);
    }

    public function fetch_trades(string $symbol, ?int $since = null, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * get the list of most recent $trades for a particular $symbol
             * @see https://bybit-exchange.github.io/docs/v5/market/recent-trade
             * @param {string} $symbol unified $symbol of the $market to fetch $trades for
             * @param {int|null} $since timestamp in ms of the earliest trade to fetch
             * @param {int|null} $limit the maximum amount of $trades to fetch
             * @param {array} $params extra parameters specific to the bybit api endpoint
             * @return {[array]} a list of ~@link https://docs.ccxt.com/en/latest/manual.html?#public-$trades trade structures~
             */
            $this->check_required_symbol('fetchTrades', $symbol);
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
                // 'baseCoin' => '', // Base coin. For option only. If not passed, return BTC data by default
                // 'optionType' => 'Call', // Option type. Call or Put. For option only
            );
            if ($limit !== null) {
                // spot => [1,60], default => 60.
                // others => [1,1000], default => 500
                $request['limit'] = $limit;
            }
            if ($market['type'] === 'spot') {
                $request['category'] = 'spot';
            } else {
                if ($market['option']) {
                    $request['category'] = 'option';
                } elseif ($market['linear']) {
                    $request['category'] = 'linear';
                } elseif ($market['inverse']) {
                    $request['category'] = 'inverse';
                }
            }
            $response = Async\await($this->publicGetV5MarketRecentTrade (array_merge($request, $params)));
            //
            //     {
            //         "retCode" => 0,
            //         "retMsg" => "OK",
            //         "result" => {
            //             "category" => "spot",
            //             "list" => array(
            //                 array(
            //                     "execId" => "2100000000007764263",
            //                     "symbol" => "BTCUSDT",
            //                     "price" => "16618.49",
            //                     "size" => "0.00012",
            //                     "side" => "Buy",
            //                     "time" => "1672052955758",
            //                     "isBlockTrade" => false
            //                 }
            //             )
            //         ),
            //         "retExtInfo" => array(),
            //         "time" => 1672053054358
            //     }
            //
            $result = $this->safe_value($response, 'result', array());
            $trades = $this->safe_value($result, 'list', array());
            return $this->parse_trades($trades, $market, $since, $limit);
        }) ();
    }

    public function fetch_order_book(string $symbol, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $limit, $params) {
            /**
             * fetches information on open orders with bid (buy) and ask (sell) prices, volumes and other data
             * @see https://bybit-exchange.github.io/docs/v5/market/orderbook
             * @param {string} $symbol unified $symbol of the $market to fetch the order book for
             * @param {int|null} $limit the maximum amount of order book entries to return
             * @param {array} $params extra parameters specific to the bybit api endpoint
             * @return {array} A dictionary of ~@link https://docs.ccxt.com/#/?id=order-book-structure order book structures~ indexed by $market symbols
             */
            $this->check_required_symbol('fetchOrderBook', $symbol);
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            $defaultLimit = 25;
            if ($market['spot']) {
                // $limit => [1, 50]. Default => 1
                $defaultLimit = 50;
                $request['category'] = 'spot';
            } else {
                if ($market['option']) {
                    // $limit => [1, 25]. Default => 1
                    $request['category'] = 'option';
                } elseif ($market['linear']) {
                    // $limit => [1, 200]. Default => 25
                    $request['category'] = 'linear';
                } elseif ($market['inverse']) {
                    // $limit => [1, 200]. Default => 25
                    $request['category'] = 'inverse';
                }
            }
            $request['limit'] = ($limit !== null) ? $limit : $defaultLimit;
            $response = Async\await($this->publicGetV5MarketOrderbook (array_merge($request, $params)));
            //
            //     {
            //         "retCode" => 0,
            //         "retMsg" => "OK",
            //         "result" => array(
            //             "s" => "BTCUSDT",
            //             "a" => array(
            //                 array(
            //                     "16638.64",
            //                     "0.008479"
            //                 )
            //             ),
            //             "b" => array(
            //                 array(
            //                     "16638.27",
            //                     "0.305749"
            //                 )
            //             ),
            //             "ts" => 1672765737733,
            //             "u" => 5277055
            //         ),
            //         "retExtInfo" => array(),
            //         "time" => 1672765737734
            //     }
            //
            $result = $this->safe_value($response, 'result', array());
            $timestamp = $this->safe_integer($result, 'ts');
            return $this->parse_order_book($result, $symbol, $timestamp, 'b', 'a');
        }) ();
    }

