            $url = $this->urls['api']['ws'];
            $requestId = $this->request_id();
            $name = 'kline';
            $messageHash = $name . ':' . $timeframe . ':' . $symbol;
            $method = $name . '.subscribe';
            $subscribe = array(
                'method' => $method,
                'id' => $requestId,
                'params' => [
                    $market['id'],
